            context.Animals.Add(new Animal // 2
            {
                TypeOfAnimalId = context.TypeOfAnimals.First(x => x.Name == "Собаки").Id,
                Name = "Золотистый ретривер",
                ShortDescription = "Золоти́стый ретри́вер — порода охотничьих собак, выведенная в Великобритании в XIX веке.",
                Description = "Золотистый ретривер был выведен в Англии как универсальная охотничья собака для подачи битой дичи. Имеет одних и тех же предков с лабрадором. По одной из версий, не имеющей, к слову, никаких документальных подтверждений, появление породы связано с именем лорда Твидмаута, сэра Дадли Маджорибенкса. В 1858 году он побывал в Брайтоне на представлении заезжего цирка, где в одном из номеров увидел собак, поразивших его своим интеллектом. Он был настолько покорён, что сразу же приобрёл в цирке восемь собак.",
                Appearance = "Собака гармоничного телосложения, с крепкими и мускулистыми конечностями и округлыми лапами. Голова пропорциональна корпусу, теменная часть широкая. Хорошо выражен переход ото лба к морде. Мочка носа чёрная. Глаза тёмно-коричневого цвета, широко расставлены, с тёмными краями век. Уши среднего размера, висячие, посажены примерно на уровне глаз.",
                Habitat = "Она одинаково будет служить каждому из членов семьи, одаривая своей лаской и любовью и взрослых, и детей. В отношениях с родными людьми, можно сказать, с данной породой как будто в принципе невозможны конфликтные ситуации, настолько они добрые и милые.",
                ImagePath = @"https://proporodysobak.ru/wp-content/uploads/2017/01/golden-retriever-1059490_640-640x390.jpg",
